/* eslint-disable react/jsx-no-target-blank */
import React from 'react'
import {useEffect, useState} from 'react';
import git from './img'

const Portifolio = () => {
    const [repositories, setRepositories]= useState([])
	useEffect(()=>{
		fetch("https://api.github.com/users/PatriciaRuffino/repos")
	.then(response => response.json())
	.then(data => setRepositories(data))
	}, [])
    return (
		<>
		<div className = "card text-center">
			<div className= "overflow">
				<img scr = {git} alt="git foto"/>
			</div>


		</div>
		<div className= "container-fluid d-flex justify-content-center">
			 <div className="row">
			 <div className="col-md-4">
		<div className="card-body text-dark">
			{repositories.map(repository => {
				return(
					<div>

					<h4 className="card-title">{repository.name}</h4>
					<p className= "card-text text-secondary">{repository.description}</p>
					<a href={repository.html_url} className= "btn btn-outline-suc" target="_blank">Saiba mais</a>

					</div>

				)
			})}
				</div>
			</div>
		</div>
		</div>
		</>


    )
}

export default Portifolio
